%% Load annotations and connectors

% Load annotations json. Generated by Wei's code gen_annotation_map.py
annotations=loadjson('/home/simulation/tracing/sid_by_annotation.json');

% Return all skeleton IDs for R and L ORNs

ORNs_Left=annotations.Left_0x20_ORN;
ORNs_Right=annotations.Right_0x20_ORN;

ORNs=[ORNs_Left, ORNs_Right];

%return all skeleton IDs of DM6 PNs
PNs=annotations.DM6_0x20_PN;


%Load the connector structure
load('/home/simulation/tracing/conns.mat')

%gen conn fieldname list
connFields=fieldnames(conns);

%% Collect simulation results for PN1LS

pn1LS_Vm=importdata('/home/simulation/nC_projects/PN1LS_allORNs/simulations/unitaries/neuron_PN1_LS_sk_419138_0.dat');
pn1LS_simTime=importdata('/home/simulation/nC_projects/PN1LS_allORNs/simulations/unitaries/time.dat');

%collect ORN skel IDs from PN1LS hoc file and save them in a txt file
idCommand='grep -Po ''(?<=objref spikesource_)\d*'' /home/simulation/nC_projects/PN1LS_allORNs/simulations/unitaries/PN1LS_allORNs.hoc > /home/simulation/nC_projects/PN1LS_allORNs/simulations/unitaries/ornIDs.txt ';
system(idCommand);
ornSkelIDs=importdata('/home/simulation/nC_projects/PN1LS_allORNs/simulations/unitaries/ornIDs.txt');

fireTimeCmd='grep -Po ''((?<=.start = )\d*)'' /home/simulation/nC_projects/PN1LS_allORNs/simulations/unitaries/PN1LS_allORNs.hoc > /home/simulation/nC_projects/PN1LS_allORNs/simulations/unitaries/ornSpikeTimes.txt';
system(fireTimeCmd);
fireTimes=importdata('/home/simulation/nC_projects/PN1LS_allORNs/simulations/unitaries/ornSpikeTimes.txt');


%% Collect uEPSPs in an array

leftCounter=1;
rightCounter=1;

for o=1:length(ornSkelIDs)
    
    if ismember(ornSkelIDs(o),ORNs_Left) == 1
        
        leftUEPSPs{1}(leftCounter,:)= pn1LS_Vm(find(pn1LS_simTime==fireTimes(o))-160:find(pn1LS_simTime==fireTimes(o))+7840);
        leftContactNum{1}(leftCounter)=getSynapseNum(ornSkelIDs(o),PNs(2));
        
        %store the skeleton ID associated with each unitary in leftUEPSPs_idList
        leftUEPSPs_idList{1}(leftCounter)=ornSkelIDs(o);
        leftCounter=leftCounter+1;
        
    elseif ismember(ornSkelIDs(o),ORNs_Right) == 1
        
        rightUEPSPs{1}(rightCounter,:)= pn1LS_Vm(find(pn1LS_simTime==fireTimes(o))-160:find(pn1LS_simTime==fireTimes(o))+7840);
        rightContactNum{1}(rightCounter)=getSynapseNum(ornSkelIDs(o),PNs(2));
        
        rightUEPSPs_idList{1}(rightCounter)=ornSkelIDs(o);
        rightCounter=rightCounter+1;
        
    end
end

