
function [] = getLNConnNums(lnNum)

addpath(genpath('/home/wft2/Matlab'));

% Load annotations json. Generated by Wei's code gen_annotation_map.py
annotations=loadjson('~/tracing/sid_by_annotation.json');

% Return all skeleton IDs for R and L ORNs

ORNs_Left=annotations.Left_0x20_ORN;
ORNs_Right=annotations.Right_0x20_ORN;

ORNs=[ORNs_Left, ORNs_Right];

%return all skeleton IDs of DM6 PNs
PNs=sort(annotations.DM6_0x20_PN);


% return all skel IDs with *LN* in fieldname
Fn = fieldnames(annotations);
selFn = Fn(~cellfun(@isempty,regexp(Fn,'LN')));

LNs=[];

for i = 1:numel(selFn)
    LNs=[LNs, annotations.(selFn{i})];
end

LNs = unique(LNs);

%Load the connector structure
load('~/tracing/conns.mat')

%gen conn fieldname list
connFields=fieldnames(conns);


  
    % get contact number between each PN and each PN
    %loop over PNs
    for p=1:length(PNs)
        
        lnToPn(p)=getSynapseNum(LNs(lnNum),PNs(p));
    end
    
    % get contact number between each PN and each LN
    %loop over LNs
    for l=1:length(LNs)
        
        lnToLn(l)=getSynapseNum(LNs(lnNum),LNs(l));
    end
    
    
    % get contact number between each PN and each ORN
    %loop over ORNs
    for orn=1:length(ORNs)
        
        lnToOrn(orn)=getSynapseNum(LNs(lnNum),ORNs(orn));
    end
    
    
    
cd('~/tracing/connectionNums/LNs/')

save([num2str(LNs(lnNum)),'_ToPNs'],'lnToPn')
save([num2str(LNs(lnNum)),'_ToORNs'],'lnToOrn')    
save([num2str(LNs(lnNum)),'_ToLNs'],'lnToLn')    

end